CREATE TABLE IF NOT EXISTS `studio_dher` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `m_id` tinyint(3) unsigned NOT NULL COMMENT '类型',
  `name` char(32) NOT NULL COMMENT '用户名',
  `psw` char(32) NOT NULL COMMENT '密码',
  `lasttime` int(10) unsigned NOT NULL COMMENT '最后登录',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态',
  `mail` char(64) NOT NULL COMMENT '邮箱',
  `rname` char(64) NOT NULL COMMENT '真实姓名',
  `qq` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`),
  KEY `m_id` (`m_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ROW_FORMAT=FIXED AUTO_INCREMENT=5 ;

